
public class SwapNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b=25;
		int d=50;
		System.out.println("Before swaping,, The numbers are "+b+" and "+d);
		int z;
		z=b;
		b=d;
		d=z;
		System.out.println("after swaping,, The numbers are "+b+" and "+d);
	}
	

}
